import axios from "axios";
import axiosClient from "./axiosClient";
const END_POINT = {
  LOGIN: "User/Login",
  SIGNUP: "User/MemberRegister",
};

export const handleLoginApi = (userEmail, userPassword) => {
  return axiosClient.post(`${END_POINT.LOGIN}`, {
    email: userEmail,
    password: userPassword,
  });
};

export const handleSignUpApi = async (user) => {
  try {
    return await axiosClient
      .post(`${END_POINT.SIGNUP}`, {
        email: user.email,
        password: user.password,
        firstName: user.firstName,
        lastName: user.lastName,
        phone: user.phone,
      })
      .then((response) => {
        console.log(response);
        console.log("ad");
      })
      .catch((error) => {
        console.log(error.message); // In ra thông báo lỗi
        console.log(error.response.status); // In ra mã trạng thái
        console.log(error.response.data); // In ra dữ liệu response (nếu có)
        console.log("co loi");
      });
  } catch (error) {}
};
