import axios from "axios";
import axiosClient from "./axiosClient";
const END_POINT = {
  LOGIN: "User/Login",
  SIGNUP: "User/MemberRegister",
};

export const handleLoginApi = (userEmail, userPassword) => {
  return axiosClient.post(`${END_POINT.LOGIN}`, {
    email: userEmail,
    password: userPassword,
  });
};

// export const handleSignUpApi = (user) => {
//   const response = axiosClient.post(`${END_POINT.SIGNUP}`, {
//     email: user.email,
//     password: user.password,
//     firstName: user.firstName,
//     lastName: user.lastName,
//     phone: user.phone,
//   });
//   console.log(response.data);
//   return response.data;
// };

export const handleSignUpApi = async (user) => {
  try {
    const response = await axiosClient.post(`${END_POINT.SIGNUP}`, {
      email: user.email,
      password: user.password,
      firstName: user.firstName,
      lastName: user.lastName,
      phone: user.phone,
    });
    console.log(response); // This will now log the actual response, not the promise.
    return response.data; // Return the resolved response data.
  } catch (error) {
    console.error("Error during sign up:", error);
    throw error;
  }
};
