import { Suspense, startTransition } from "react";
import PropTypes from "prop-types";
import { useState, useEffect } from "react";

const LoadLazy = ({ children }) => {
  const [isPending, setIsPending] = useState(false);

  useEffect(() => {
    startTransition(() => {
      setIsPending(true);
      // Simulate an async operation (e.g., data fetch, lazy loading)
      const timer = setTimeout(() => {
        setIsPending(false);
      }, 1000); // Adjust the time as needed for actual loading

      return () => clearTimeout(timer);
    });
  }, []);

  return (
    <div>
      {isPending ? (
        <div className="fixed flex justify-center items-center text-9xl top-0 bottom-0 left-0 right-0">
          Loading...
        </div>
      ) : (
        <Suspense
          fallback={
            <div className="fixed flex justify-center items-center text-9xl top-0 bottom-0 left-0 right-0">
              Loading...
            </div>
          }
        >
          {children}
        </Suspense>
      )}
    </div>
  );
};

LoadLazy.propTypes = {
  children: PropTypes.node.isRequired,
};

export default LoadLazy;
