import React, { useRef } from "react";
import { useImageUpload } from "./UploadFunction";

const UploadButton = () => {
  const { imgUrl, setImg, handleUpload } = useImageUpload();
  const fileInputRef = useRef(null); // Reference to the hidden input

  const triggerFileSelect = () => {
    fileInputRef.current.click(); // Trigger file selection dialog
  };

  return (
    <div>
      <button onClick={triggerFileSelect}>Select & Upload Image</button>
      <input
        type="file"
        style={{ display: "none" }} // Hide the input element
        ref={fileInputRef}
        onChange={(e) => setImg(e.target.files[0])}
      />
      <button onClick={handleUpload}>Upload</button>
      <div>
        {imgUrl.map((url, index) => (
          <div key={index}>
            <h4>{url}</h4>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UploadButton;
