import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import { handlePayCallBack } from "../../../axios/UserService";
import "./PaymentResponse.scss";

const PaymentResponse = () => {
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(true);
  const [searchParams] = useSearchParams();

  const vnp_Amount = searchParams.get("vnp_Amount");
  const vnp_OrderInfo = searchParams.get("vnp_OrderInfo");
  const vnp_PayDate = searchParams.get("vnp_PayDate");
  const vnp_ResponseCode = searchParams.get("vnp_ResponseCode");

  const PayCallBack = async (info) => {
    const res = await handlePayCallBack(info);
    // console.log(res);
    setResult(res);
  };
  useEffect(() => {
    const info = { vnp_Amount, vnp_OrderInfo, vnp_PayDate, vnp_ResponseCode };

    // PayCallBack(info);
    setTimeout(() => setLoading(false), 100000);
  });
  return (
    <div className="screen">
      {loading ? (
        <div>
          <div className="title-waiting">Pending payment...</div>
          <img
            src="https://cdn.dribbble.com/users/2077073/screenshots/7103423/media/223825c7f91575b639fd71f3accff8f4.gif"
            alt=""
          />
        </div>
      ) : result.status === 200 ? (
        <p>success</p>
      ) : (
        <p>fail</p>
      )}
    </div>
  );
};

export default PaymentResponse;
