import axios from "axios";
import axiosClient from "./axiosClient";
const END_POINT = {
  LOGIN: "User/Login",
  SIGNUP: "User/MemberRegister",
};

export const handleLoginApi = (userEmail, userPassword) => {
  return axiosClient.post(`${END_POINT.LOGIN}`, {
    email: userEmail,
    password: userPassword,
  });
};

export const handleSignUpApi = async (user) => {
  try {
    const response = await axiosClient.post(`${END_POINT.SIGNUP}`, {
      email: user.email,
      password: user.password,
      firstName: user.firstName,
      lastName: user.lastName,
      phone: user.phone,
    });

    if (response.status === 400) {
      const errorMessage =
        response.data.message || "An error occurred during signup.";
      console.log("Error message:", errorMessage);
      // Handle the error appropriately (e.g., display an error message to the user)
    } else if (response.status === 200 || response.status === 201) {
      console.log("Signup successful:", response.data);
      // Handle successful signup logic here (e.g., redirect to success page)
    } else {
      throw new Error(`API error: ${response.status} - ${response.statusText}`);
    }
  } catch (error) {
    console.error("Error during signup:", error);
    // Handle signup errors appropriately (e.g., display an error message to the user)
  }
};
